"""
Built-in, globally-available admin actions.
"""

from django.contrib import messages
from django.contrib.admin import helpers
from django.contrib.admin.decorators import action
from django.contrib.admin.utils import model_ngettext
from django.core.exceptions import PermissionDenied
from django.template.response import TemplateResponse
from django.utils.translation import gettext as _
from django.utils.translation import gettext_lazy


@action(
    permissions=["change"],
    description=gettext_lazy("Mark selected %(verbose_name_plural)s as paid"),
)
def mark_as_paid(modeladmin, request, queryset):
    """
    Mark as paid action which marks the selected objects as paid.

    This action first displays a confirmation page which shows all the
    objects that will be marked as paid.

    Next, it marks all selected objects as paid and redirects back to the
    change list.
    """
    opts = modeladmin.model._meta
    app_label = opts.app_label

    if request.POST.get("post"):
        n = queryset.count()
        if n:
            for obj in queryset:
                obj.is_paid = True
                obj.save()
            modeladmin.message_user(
                request,
                _("Successfully marked %(count)d %(items)s as paid.")
                % {"count": n, "items": model_ngettext(modeladmin.opts, n)},
                messages.SUCCESS,
            )
        # Return None to display the change list page again.
        return None

    objects_name = model_ngettext(queryset)

    context = {
        **modeladmin.admin_site.each_context(request),
        "title": _("Are you sure?"),
        "subtitle": _("Mark as paid"),
        "objects_name": str(objects_name),
        "queryset": queryset,
        "opts": opts,
        "action_checkbox_name": helpers.ACTION_CHECKBOX_NAME,
        "media": modeladmin.media,
    }

    request.current_app = modeladmin.admin_site.name

    # Display the confirmation page
    return TemplateResponse(
        request,
        modeladmin.mark_as_paid_confirmation_template
        or [
            "admin/%s/%s/mark_as_paid_confirmation.html" % (app_label, opts.model_name),
            "admin/%s/mark_as_paid_confirmation.html" % app_label,
            "admin/mark_as_paid_confirmation.html",
        ],
        context,
    )


